    List<Productwaarde> col1Items = new List<Productwaarde>();
    List<Productwaarde> col2Items = new List<Productwaarde>();
    
    int currentColumn = 0;
    foreach (Productwaarde item in Model.Page())
    {
        switch (currentColumn)
        {
            case 0:
                col0Items.Add(item);
                currentColumn++;
                break;
            case 1:
                col1Items.Add(item);
                currentColumn++;
                break;
            case 2:
                col2Items.Add(item);
                // Since this is the last column, we reset back to first column
                currentColumn = 0;
                break;
        }
    }
 

